mapper.select:
select rno, adminBoard_idx, name, title, content, fk_sellerName, fk_hotelName, registerday , commentCount, groupno, fk_seq, depthno , fileName, orgFilename, fileSize ,fk_adminId from ( select rownum AS rno,fk_adminId, adminBoard_idx, name, title, content, fk_sellerName, fk_hotelName, registerday , commentCount, groupno, fk_seq, depthno , fileName, orgFilename, fileSize from ( select adminBoard_idx, name, title, content, fk_sellerName, fk_hotelName, to_char(registerday, 'yyyy-mm-dd hh24:mi:ss') as registerday , commentCount, groupno, fk_seq, depthno , fileName, orgFilename, fileSize,fk_adminId from tbl_here_adminBoard where status = 1
and ${searchType} like '%'|| #{searchWord} ||'%'
start with fk_seq = 0 connect by prior adminBoard_idx = fk_seq order siblings by groupno desc, adminBoard_idx asc ) V ) T where rno between #{startRno} and #{endRno}

mapper.update:
update tbl_here_adminBoard set title = #{title} , content = #{content}, fk_sellerName = #{fk_sellerName}, fk_hotelName = #{fk_hotelName}
                               ,fileName = #{fileName}, orgFilename = #{orgFilename}, fileSize = #{fileSize} 
where adminBoard_idx = #{adminBoard_idx}

mapper.select:

    	select name, fk_adminId, content, to_char(registerday, 'yyyy-mm-dd hh24:mi:ss') as registerday, adminComment_idx,fk_parentIdx
    	from tbl_here_adminComment
    	where adminComment_idx = #{adminComment_idx}

select count(*) from ( select member_idx, email, pwd, name, nickname, hp1,hp2, hp3, gender, trunc(age,-1) AS age, mstatus, registerday, lastlogindate, lastpwdchangedate from ( select member_idx, email, pwd, name, nickname, hp1,hp2, hp3, gender, extract(year from sysdate) - to_number(substr(birthday,1,4)) AS age, mstatus,to_char(registerday,'yyyymmdd') AS registerday, lastlogindate, lastpwdchangedate from tbl_here_member )
where trunc(age,-1) in (10,20,30,40,50,60,70,80,90)
where trunc(age,-1) in (
#{age}
)
and gender = #{gender}
and mstatus = #{mstatus}
and ${searchType} like '%'|| #{searchWord} ||'%'
and registerday between to_date(#{startDate},'yyyy-mm-dd') and to_date(#{endDate},'yyyy-mm-dd')+0.99999
)

select rno, member_idx, email, pwd, name, nickname, hp1,hp2, hp3, gender, age, mstatus, registerday, lastlogindate, lastpwdchangedate, totalPrice, totalCount from ( select rownum AS rno, member_idx, email, pwd, name, nickname, hp1,hp2, hp3, gender, age, mstatus, registerday, lastlogindate, lastpwdchangedate, totalPrice, totalCount from ( select member_idx, email, pwd, name, nickname, hp1,hp2, hp3, gender, age, mstatus, to_char(registerday,'yyyy-mm-dd') AS registerday, lastlogindate, lastpwdchangedate, nvl(totalPrice,0) AS totalPrice, nvl(totalCount,0) AS totalCount from ( select member_idx, email, pwd, name, nickname, hp1,hp2, hp3, gender, age, mstatus, registerday, lastlogindate, lastpwdchangedate from ( select member_idx, email, pwd, name, nickname, hp1,hp2, hp3, gender, extract(year from sysdate) - to_number(substr(birthday,1,4)) AS age, mstatus,registerday, lastlogindate, lastpwdchangedate from tbl_here_member )
where trunc(age,-1) in (10,20,30,40,50,60,70,80,90)
where trunc(age,-1) in (
#{age}
,80,90
)
and gender = #{gender}
and mstatus = #{mstatus}
and ${searchType} like '%'|| #{searchWord} ||'%'
and to_char(registerday,'yyyymmdd') between to_date(#{startDate},'yyyy-mm-dd') and to_date(#{endDate},'yyyy-mm-dd')+0.99999
) M left outer join (select sum(res_totalPrice) AS totalPrice, fk_member_idx, count(*) AS totalCount from tbl_here_reservation group by fk_member_idx ) R on M.member_idx = R.fk_member_idx order by ${orderType} desc ) )
where rno between #{startRno} and #{endRno}

      	select to_char(max(res_paymentDay),'yyyy-mm-dd') AS lastDate
		from tbl_here_reservation
		where fk_member_idx = #{member_idx}
