mapper.update:
update tbl_here_admin_event set event_name = #{event_name} , event_discountMon = #{event_discountMon} 
, event_discountPct = #{event_discountPct}	
, event_startday = to_date(#{event_startday}, 'yyyy-mm-dd')	
, event_endday = to_date(#{event_endday}, 'yyyy-mm-dd')	
, event_explain = #{event_explain}
where event_code = #{event_code} and fk_admin_idx = #{fk_admin_idx}


update tbl_here_mpoint 
set mpointCash = mpointCash + ( select coupon_discountmon from tbl_here_admin_coupon where coupon_code = #{fk_coupon_code} )
where fk_member_idx = #{fk_idx}

mapper.select:
select X.* from ( select row_number() over(order by no desc) as rno, V.* from ( select T.* from ( select a.no as no , a.fk_idx as fk_idx , a.type as type , a.phone as phone , a.content as content , to_char(a.writeday,'yyyy-mm-dd') as writeday , a.status as status , b.no as rep_no , b.fk_no as rep_fk_no , c.name as rep_admin_idx , b.content as rep_content , to_char(b.writeday,'yyyy-mm-dd hh24:mi:ss') as rep_writeday , b.status as rep_status from ( select * from tbl_here_faq where status = 1
and type = #{category}
and sysdate-writeday between 0 and 7
and sysdate-writeday between 0 and 30
order by no desc ) A left join ( select * from tbl_here_faq_reply where status = 1 ) B on a.no = b.fk_no left join tbl_here_adminmember C on b.fk_admin_idx = c.admin_idx ) T
where rep_no is null
where rep_no is not null
) V ) X where rno between #{startRno} and #{endRno}
