mapper.select:
select *
from tbl_here_hotelInfo
where hotel_Status = 1 and hotel_idx = #{hotel_idx}

select review_no
from tbl_here_review
where status = 1 and fk_seq = #{seq}

mapper.delete:
delete from tbl_here_review_img where fk_review_no = #{review_no}
and not orgImgname in
('${orgAttachArr[i]}')

mapper.update:
		
		
		update tbl_here_review set title = #{title}
                             , content = #{content}
                             , grade0 = #{grade0}
                             , grade1 = #{grade1}
                             , grade2 = #{grade2}
                             , grade3 = #{grade3}
                             , grade4 = #{grade4}
        
        
        
		update tbl_here_review set status = 0
                             
		where review_no = #{review_no}
